import cocinero from "./images/cocinero.png";
import guardaespaldas from "./images/guardaespaldas.png";
import camarera from "./images/camarera.png";
import { Fragment } from "react";
import Button from "@mui/material/Button";
import React, { useState } from "react";
import TextField from "@mui/material/TextField";
import InputAdornment from "@mui/material/InputAdornment";
import { signInWithEmailAndPassword, auth } from "../lib/firebaseConfig";
import { iconoPassword } from "./images/iconoPassword";
import { iconoNotPassword } from './images/iconoNotPassword'
import {emailIcon} from './images/emailIcon'


function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [visible, setVisible] = useState(false);
  const toggleVisibility = () => {
    setVisible(!visible);
  };



  const enviarDatos = (event) => {
    event.preventDefault();
    console.log(email, password);
    signInWithEmailAndPassword(auth, email, password)
      .then((userCredential) => {
        // Signed in
        const user = userCredential.user;
        console.log(user.uid);
        window.location.href = "/menu";

        // ...
      })
      .catch((error) => {
        const errorCode = error.code;
        const errorMessage = error.message;
        console.log(errorCode, errorMessage);
      });
  };

  return (
    <Fragment>
      <main>
        <div className="Usuarios">
          <img src={guardaespaldas} alt=""></img>
          <img src={camarera} alt=""></img>
          <img src={cocinero} alt=""></img>
        </div>
        <form onSubmit={enviarDatos}>
          <h1>Inicia Sesi칩n</h1>
          <TextField
            fullWidth
            autoFocus
            color="secondary"
            margin="normal"
            variant="outlined"
            name="email"
            type="email"
            label="Correo electr칩nico"
            onChange={(e) => setEmail(e.target.value)}
            required
            InputProps={{
              endAdornment: (
                <InputAdornment position="end">
                 {emailIcon}
                </InputAdornment>
              ),
            }}
          />
          <TextField
            fullWidth
            color="secondary"
            margin="normal"
            variant="outlined"
            name="password"
            type={visible ? "text" : "password"}
            label="Contrase침a"
            onChange={(e) => setPassword(e.target.value)}
            required
            InputProps={{
              endAdornment: (
                <InputAdornment position="end" onClick={toggleVisibility}>
                  {visible ? iconoPassword : iconoNotPassword}
                </InputAdornment>
              ),
            }}
          />

          <Button
            fullWidth
            id="Login"
            variant="contained"
            margin="large"
            type="submit"
          >
            Iniciar Sesi칩n
          </Button>
        </form>
      </main>
    </Fragment>
  );
}
export default Login;
